glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
#if USE_OLD_PHOTO
uniform OldPhoto {
  float oldLevel;
};
vec4 getOldPhotoColor(vec4 color) {
  float r = 0.393 * color.r + 0.769 * color.g + 0.189 * color.b;
  float g = 0.349 * color.r + 0.686 * color.g + 0.168 * color.b;
  float b = 0.272 * color.r + 0.534 * color.g + 0.131 * color.b;
  return vec4(r, g, b, color.a);
}
#endif
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
  o *= texture(texture, v_uv0);
    #if CC_USE_ALPHA_ATLAS_TEXTURE
    o.a *= texture2D(texture, v_uv0 + vec2(0, 0.5)).r;
    #endif
  #endif
  o *= v_color;
  ALPHA_TEST(o);
  #if USE_OLD_PHOTO
  vec4 srcColor = o;
  vec4 oldColor = getOldPhotoColor(srcColor);
  o = mix(srcColor, oldColor, oldLevel);
  #endif
  gl_FragColor = o;
}

